// Parse AI Agent JSON response
const items = $input.all();
const parsedItems = [];

for (const item of items) {
  const rawResponse = item.json.output;

  try {
    // Clean up escaped characters and parse the JSON response from AI
    const cleanedResponse = rawResponse.replace(/\\n/g, '').replace(/\\/g, '');
    const aiAnalysis = JSON.parse(cleanedResponse);

    // Handle key_topics - it might be a string or array
    let topicsString = "";
    if (Array.isArray(aiAnalysis.key_topics)) {
      topicsString = aiAnalysis.key_topics.join(", ");
    } else if (typeof aiAnalysis.key_topics === 'string') {
      topicsString = aiAnalysis.key_topics;
    }

    // Combine original article data with parsed AI analysis
    parsedItems.push({
      json: {
        // Original article data
        title: $node["RSS Feed Trigger"].json.title,
        link: $node["RSS Feed Trigger"].json.link,
        pubDate: $node["RSS Feed Trigger"].json.pubDate,
        content: $node["RSS Feed Trigger"].json['content:encoded'],

        // Parsed AI analysis
        quality_score: aiAnalysis.quality_score || 5,
        relevance_score: aiAnalysis.relevance_score || 5,
        key_topics: topicsString,
        summary: aiAnalysis.summary || "No summary provided",
        content_angle: aiAnalysis.content_angle || "No angle provided",
        action: aiAnalysis.action || "REVIEW"
      }
    });

  } catch (error) {
    // Handle parsing errors gracefully
    console.log("Parsing error:", error.message);
    console.log("Raw response:", rawResponse);

    parsedItems.push({
      json: {
        title: $node["RSS Feed Trigger"].json.title,
        link: $node["RSS Feed Trigger"].json.link,
        pubDate: $node["RSS Feed Trigger"].json.pubDate,
        content: $node["RSS Feed Trigger"].json['content:encoded'],
        quality_score: 5,
        relevance_score: 5,
        key_topics: "Parse Error",
        summary: "AI response could not be parsed: " + error.message,
        content_angle: "Manual review needed",
        action: "REVIEW"
      }
    });
  }
}

return parsedItems;

// Parse content creation AI response
const items = $input.all();
const contentItems = [];

for (const item of items) {
  try {
    const rawResponse = item.json.output;
    const cleanedResponse = rawResponse.replace(/\\\\n/g, '').replace(/\\\\/g, '');
    const contentData = JSON.parse(cleanedResponse);

    // Get the original article data
    const originalData = $node["Code"].json; // Reference our enhanced parsing node

    contentItems.push({
      json: {
        // Original article info
        title: originalData.title,
        link: originalData.link,
        quality_score: originalData.quality_score,
        relevance_score: originalData.relevance_score,
        summary: originalData.summary,

        // Generated content
        twitter_thread: contentData.twitter || [],
        linkedin_post: contentData.linkedin_post || "Content generation failed",
        content_ready: contentData.content_ready || false,

        // Metadata
        created_at: new Date().toISOString(),
        platform_ready: true
      }
    });

  } catch (error) {
    // Handle parsing errors
    contentItems.push({
      json: {
        title: $node["Code"].json.title,
        link: $node["Code"].json.link,
        twitter_thread: ["Content generation failed"],
        linkedin_post: "Content generation failed: " + error.message,
        content_ready: false,
        error: error.message
      }
    });
  }
}

return contentItems;

